mapper.select:
SELECT *
FROM (
         SELECT ROWNUM R, TC.*
         FROM TB_COMPLAIN TC
         WHERE TC.DELETE_YN = 'N'
           AND TITLE LIKE '%' || #{title} || '%'
           AND ROWNUM < = (#{page} + 1) * #{size}
     )
WHERE R > #{page} * #{size}

SELECT *
FROM (
         SELECT ROWNUM R, TC.*
         FROM TB_COMPLAIN TC
         WHERE TC.DELETE_YN = 'N'
         AND ROWNUM < = (#{page} + 1) * #{size}
     )
WHERE R > #{page} * #{size}
